// user reducer ((((((((((( store은 정확하게는 state, action, reducer가 합쳐진 것 )))))))))))

// store
export const initialState = {
    isLoggedIn: false,
    user: {
        nickname: '박경민',
        Post: [],
        Followings: [],
        Followers: [],
    },
};

// action의 이름들
export const LOG_IN = 'LOG_IN';
export const LOG_OUT = 'LOG_OUT';

// 실제 액션들
export const loginAction = {
    type: LOG_IN,
    data: {
        nickname: 'PKM',
        Post: [],
        Followings: [],
        Followers: [],
    }
};
export const logoutAction = {
    type: LOG_OUT,
    user: null,
};

// reducer
const reducer = (state = initialState, action) => {
    switch(action.type) {
        case LOG_IN: {
            return {
                ...state,
                isLoggedIn: true,
                user: action.data,
            }
        }
        case LOG_OUT: {
            return {
                ...state,
                isLoggedIn: false,
                user: action.user,
            }
        }
        default: {
            return {
                ...state,
            }
        }
    }
};

export default reducer;
