// 레이아웃(고정)
import React from 'react';
import { Menu, Input, Row, Col, Card, Avatar } from 'antd';
import Link from 'next/link';
import PropTypes from 'prop-types'; // 부모로부터 prop을 잘 받았는지 확인하는거
import LoginForm from './LoginForm';

const dummy = {
    nickname: '박경민',
    Post: [],
    Followings: [],
    Followers: [],
    isLoggedIn: false,
};

const AppLayout = ({ children }) => {

    return (
        <div>
            <Menu mode="horizontal">
                <Menu.Item key="home"><Link href="/"><a>NodeBird</a></Link></Menu.Item>
                <Menu.Item key="profile"><Link href="/profile"><a>프로필</a></Link></Menu.Item>
                <Menu.Item key="mail">
                    <Input.Search enterButton style={{verticalAlign: 'middle'}}/>
                </Menu.Item>
            </Menu>
            <Row gutter={10}>
                <Col xs={24} md={6}>
                    {dummy.isLoggedIn ? <Card 
                        actions={[
                            <div key="twit">짹짹<br/>{dummy.Post.length}</div>,
                            <div key="followings">팔로잉<br/>{dummy.Followings.length}</div>,
                            <div key="follower">팔로워<br/>{dummy.Followers.length}</div>
                        ]}>
                        <Card.Meta avatar={<Avatar>{dummy.nickname[0]}</Avatar>} title={dummy.nickname}></Card.Meta>
                    </Card> : <LoginForm/>}
                </Col>
                <Col xs={24} md={12}>{children}</Col>
                <Col xs={24} md={6}>Made by PKM</Col>
            </Row>
        </div>    
    );
};

AppLayout.prototype = {
    Component: PropTypes.elementType,  //  jsx에 들어갈 수 있는 모든것들을 node로 침(예를 들면, 문자열, 숫자, 불리언 등등)
};

export default AppLayout;
