// 메인 페이지
import React from 'react';
import { Input, Form, Button, Card, Avatar, Icon } from 'antd';

const dummy = {
    isLoggedIn: true,
    imagePaths: [],
    mainPosts: [{
        User: {
            id: 1,
            nickname: '박경민',
        },
        content: '까불지마',
    }],
};

const Home = () => {
    return (
        <>
            {dummy.isLoggedIn && <Form encType="multipart/form-data" style={{ margin: '10px 0 20px'}}>
                    <Input.TextArea maxLength={140} placeholder="어떤 신기한 일이 있었나요?" />
                    <div>
                        <input type="file" multiple hidden/>
                        <Button>이미지 업로드</Button>
                        <Button type="primary" style={{ float: 'right' }} htmlType="submit">짹짹</Button>
                    </div>
                    <div>
                        {dummy.imagePaths.map((v, i) => {
                            return (
                                <div key={v} style={{ display: 'inline-block' }}>
                                    <img src={"http://localhost:3065/" + v} style={{ width: '200px' }} alt={v}/>
                                    <div>
                                        <Button>제거</Button>
                                    </div>
                                </div>
                            );
                        })}
                    </div>
                </Form>}
                {dummy.mainPosts.map((c) => {
                    return (
                        <Card
                            key={+c.createdAt}
                            cover={c.img && <img alt="example" src={c.img}/>}
                            actions={[
                                <Icon type="retweet" key="retweet"></Icon>,
                                <Icon type="heart" key="heart"></Icon>,
                                <Icon type="message" key="message"></Icon>,
                                <Icon type="ellipsis" key="ellipsis"></Icon>
                            ]}
                            extra={<Button>팔로우</Button>}
                        >
                            <Card.Meta
                                avatar={<Avatar>{c.User.nickname[0]}</Avatar>}
                                title={c.User.nickname}
                                description={c.content}
                            />
                        </Card>
                    );
                })}
        </>
    );
};

export default Home;
