// 메인 페이지
import React from 'react';
import { Button, Card, Avatar, Icon } from 'antd';
import PostForm from '../components/PostForm';

const dummy = {
    isLoggedIn: true,
    imagePaths: [],
    mainPosts: [{
        User: {
            id: 1,
            nickname: '박경민',
        },
        content: '까불지마',
    }],
};

const Home = () => {
    return (
        <>
            {dummy.isLoggedIn && <PostForm/>}
                {dummy.mainPosts.map((c) => {
                    return (
                        <PostCard/>
                    );
                })}
        </>
    );
};

export default Home;
