// user reducer ((((((((((( store은 정확하게는 state, action, reducer가 합쳐진 것 )))))))))))

// store
export const initialState = {
    isLoggedIn: false,
    user: {
        nickname: '박경민',
        Post: [1],
        Followings: [1],
        Followers: [1],
        isLoggedIn: false,
    },
};

// action의 이름들
export const LOG_IN = 'LOG_IN';
export const LOG_OUT = 'LOG_OUT';

// 실제 액션들
const loginAction = {
    type: LOG_IN,
    data: {
        nickname: 'PKM'
    }
};
const logoutAction = {
    type: LOG_OUT,
};

// reducer
const reducer = (state = initialState, action) => {
    switch(action.type) {
        case LOG_IN: {
            return {
                ...state,
                isLoggedIn: true,
                user: action.data,
            }
        }
        case LOG_OUT: {
            return {
                ...state,
                isLoggedIn: false,
                user: null,
            }
        }
        default: {
            return {
                ...state,
            }
        }
    }
};

export default reducer;
